import React from 'react';

function Tile(props) {
    return (
      <button className="tile" onClick={props.onClick}>
        <svg height="34" width="34">
        <circle cx="17" cy="17" r={props.stoneRadius} stroke="black" strokeWidth="1" fill={props.stoneFill} />
        </svg>

      </button>
    );
}


class Board extends React.Component {
  renderTile(row,col) {
    return (
      <Tile 
      onClick={()=>this.props.onClick(row,col)}
      stoneRadius={this.props.board[row][col]!=='n' ? 14:0}
      stoneFill={this.props.board[row][col]==='b' ? 'black':'white'}
      />
    );

  }
  render() {
    const board=[];
    for(let row=0;row<this.props.boardSize;row++) {
      const boardRow=[];
      for(let col=0;col<this.props.boardSize;col++) {
        boardRow.push(this.renderTile(row,col));
      }
      board.push(<div className="board-row">{boardRow}</div>)
    }
    return (
      <div>
        {board}
      </div>
    );
  }
}

//Handles Game Logic
class Game extends React.Component {
  constructor(props) {
    super(props);
    this.boardSize=9;

    let Board=new Array(this.boardSize);
    for(let row=0;row<this.boardSize;row++) {
      Board[row]=new Array(this.boardSize);
    }
    for(let row=0;row<this.boardSize;row++) {
      for(let col=0;col<this.boardSize;col++) {
        Board[row][col]='n';
      }
    }
    this.state={
      currentPlayer:'b',
      board:Board,
      boardSize:this.boardSize,
      playerTurnStatus:"Black's Turn!",
      errorMessage:""
    };
  }


  isValidPos(row,col) {
    if(row>=0 && row<this.boardSize) {
      if(col>=0 && col<this.boardSize) {
        return true;
      }
    }
    return false;
  }

  //returns the number of liberties that a stone or group of connected stones has
  //expects the row and col of the stone or a row col in the group  as input
  captureCheck(board,row,col,checkedNeighbors) {
    const color=board[row][col]
    const directions=[[-1,0],[1,0],[0,1],[0,-1]];
    let numOfLiberties=0;
    let neighbors=[];
    let strRowCol=(row.toString())+"."+(col.toString());
    for(let i=0;i<directions.length;i++) {
      let direction=directions[i];
      let adjRow=row+direction[0];
      let adjCol=col+direction[1];
      if(this.isValidPos(adjRow,adjCol)) {
        let adjValue=board[adjRow][adjCol];
        let strAdjPos=(adjRow.toString())+"."+(adjCol.toString());
        if(adjValue==='n') {
          numOfLiberties+=1;
        }
        //removed else if(adjValue===opponentColor
        else if(adjValue===color &&  !checkedNeighbors.includes(strAdjPos)) {
          neighbors.push(strAdjPos);
        }
      }
    }

    if(numOfLiberties===0 && neighbors.length!==0) {
      checkedNeighbors.push(strRowCol);
      for(let i=0;i<neighbors.length;i++) {
        let neighbor=neighbors[i];
        let parsedNeighborPos=neighbor.split(".");
        let neighborRow=parseInt(parsedNeighborPos[0]);
        let neighborCol=parseInt(parsedNeighborPos[1]);
        numOfLiberties+=this.captureCheck(board,neighborRow,neighborCol,checkedNeighbors);
      }
      return numOfLiberties;
    }
    else if (numOfLiberties===0 && neighbors.length===0) {
      return 0;
    }
    else {
      return numOfLiberties;
    }
  }

  getNewBoard(i,j) {
    let newBoard=this.state.board.slice();
    newBoard[i][j]=this.state.currentPlayer;

    let rowsToRemove=[];
    let colsToRemove=[];
    const opponentColor=this.state.currentPlayer==='b' ? 'w':'b';
    console.log(opponentColor);
    for(let row=0;row<this.boardSize;row++) {
      for(let col=0;col<this.boardSize;col++) {
        const tileOccupied=newBoard[row][col]==='w' || newBoard[row][col]==='b';
        if((this.captureCheck(newBoard,row,col,[],opponentColor)===0) && tileOccupied) {
          rowsToRemove.push(row);
          colsToRemove.push(col);
        }
      }
    }
    for(let k=0;k<rowsToRemove.length;k++) {
      let rowToRemove=rowsToRemove[k];
      let colToRemove=colsToRemove[k];
      newBoard[rowToRemove][colToRemove]='n'

    }
    return newBoard

  }

  makeMove(i,j) {
    const nextPlayer=(this.state.currentPlayer==='b') ? 'w':'b';
    const newPlayerTurnStatus=(nextPlayer==='b') ? "Black's Turn!":"White's Turn!";
    const newErrorMessage="";
    const newBoard=this.getNewBoard(i,j);

    this.setState({
      board:newBoard,
      currentPlayer:nextPlayer,
      playerTurnStatus:newPlayerTurnStatus,
      errorMessage:newErrorMessage,
    });
  }

  isValidMove(row,col) {
    const testBoard=this.state.board.slice()
    testBoard[row][col]=this.state.currentPlayer;
    const currentPlayerLiberties=this.captureCheck(testBoard,row,col,[]);
    if(this.state.board[row][col]!=="n") {
      return false;
    }
    else if(currentPlayerLiberties===0) {
      return false;
    }
    return true;
  }

  handleClick(i,j) {
    if(this.isValidMove(i,j)) {
      this.makeMove(i,j);
    }
    else {
      const newErrorMessage="Invalid Move!";
      this.setState({errorMessage:newErrorMessage});
    }
  }


  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board
          board={this.state.board}
          boardSize={this.state.boardSize}
          onClick={(i,j)=>this.handleClick(i,j)}
          />
        </div>
        <div className="game-info">
          <h3>
            {this.state.playerTurnStatus}
          </h3>
          <h3>
            {this.state.errorMessage}
          </h3>
        </div>
      </div>
    );
  }
}


function App() {
  return (
    <div className="App">
      <div className="Game">
        <Game />
      </div>
    </div>
  );
}



export default App;
