import logo from './logo.svg';
import './App.css';
import React from 'react';


function Tile(props) {
    return (
      <button className="tile" onClick={props.onClick}>
        <svg height="34" width="34">
        <circle cx="17" cy="17" r={props.stoneRadius} stroke="black" stroke-width="0" fill={props.stoneFill} />
        </svg>

      </button>
    );
}


class Board extends React.Component {
  renderTile(row,col) {
    return (
      <Tile 
      onClick={()=>this.props.onClick(row,col)}
      stoneRadius={this.props.filledPositions[row][col]!='n' ? 14:0}
      stoneFill={this.props.filledPositions[row][col]=='b' ? 'black':'white'}
      />
    );

  }
  render() {
    const board=[];
    for(let row=0;row<9;row++) {
      const boardRow=[];
      for(let col=0;col<9;col++) {
        boardRow.push(this.renderTile(row,col));
      }
      board.push(<div className="board-row">{boardRow}</div>)
    }
    return (
      <div>
        {board}
      </div>
    );
  }
}

//Handles Game Logic
class Game extends React.Component {
  constructor(props) {
    super(props);
    let FilledPositions=new Array(9);
    for(let row=0;row<9;row++) {
      FilledPositions[row]=new Array(9);
    }
    for(let row=0;row<9;row++) {
      for(let col=0;col<9;col++) {
        FilledPositions[row][col]="n";
      }
    }
    this.state={
      filledPositions:FilledPositions,
      playerTurn:'b',
      playerTurnStatus:"Black's Turn!",
      errorStatus:"",
    };

  }

  //Updates Information Elements
  //Current Player's Turn
  updateVisuals() {
    const playerTurnMessage=""
    if(this.state.playerTurn==="b") {
      playerTurnMessage="Black's Turn!";
    }
    else {
      playerTurnMessage="White's Turn!";
    }
    this.setState({
      playerTurnStatus:"playerTurnMessage"
    });

  }


  makeMove(i,j) {
    const newPositions=this.state.filledPositions.slice();
    newPositions[i][j]=this.state.playerTurn;
    const newPlayerTurn=(this.state.playerTurn=='b') ? 'w':'b';
    this.setState({
      filledPositions:newPositions,
      playerTurn:newPlayerTurn
    });
  }

  //Checks if placing a black,white stone at a position is valid
  isValidMove(row,col,player) {
    //If the move is invalid
    if(this.state.filledPositions[row][col]!=="n") {
      //Display error message
      this.setState({
        status:"Invalid move! Tile is occupied"
      });
      return false;
    }
    return true

  }

  handleClick(i,j) {
    if(this.isValidMove(i,j)) {
      this.makeMove(i,j)
    }
    else {
      return (
        <header>
          Invalid Move!
        </header>
      );
    }

  }


  render() {
    this.updateVisuals();
    return (
      <div className="game">
        <div className="game-board">
          <Board
          filledPositions={this.state.filledPositions}
          onClick={(i,j)=>this.handleClick(i,j)}
          />
        </div>
        <div className="game-info">
          <h3>
            {this.state.playerTurnStatus}
          </h3>
        </div>
      </div>
    );
  }


}


function App() {
  return (
    <div className="App">
      <div className="Game">
        <Game />
      </div>
    </div>
  );
}



export default App;
