import logo from './logo.svg';
import './App.css';
import React from 'react';
import {ReactComponent as BlackStone} from './Black_Stone.svg';
import {ReactComponent as WhiteStone} from './White_Stone.svg';


function Tile(props) {
    return (
      <button className="tile" onClick={props.onClick}>
        <img
        src={BlackStone}
        style={{height:25,width:25}}
        alt="Black Stone"
        >

        </img>
      </button>


    );
}


class Board extends React.Component {
  constructor(props) {
    super(props);
    let FilledPositions=new Array(9);
    for(let row=0;row<9;row++) {
      FilledPositions[row]=new Array(9);
    }
    for(let row=0;row<9;row++) {
      for(let col=0;col<9;col++) {
        FilledPositions[row][col]="n";
      }
    }
    this.state={
      filledPositions:FilledPositions,
    }

  }
  renderTile(row,col) {
    return (
      <Tile 
      onClick={()=>this.props.onClick(row,col)}
      />
    );

  }
  render() {
    const board=[];
    for(let row=0;row<9;row++) {
      const boardRow=[];
      for(let col=0;col<9;col++) {
        boardRow.push(this.renderTile(row,col));
      }
      board.push(<div className="board-row">{boardRow}</div>)
    }
    return (
      <div>
        {board}
      </div>
    );
  }
  handleClick(i,j) {
    const newPositions=this.state.filledPositions.slice();
    newPositions[i][j]='b';
    this.setState({
      filledPositions:newPositions
    });
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);

  }
  render() {
    return (
      <div className="game">
        <div className="game-info">
        </div>
        <div className="game-board">
          <Board />
        </div>

      </div>
    );
  }


}


function App() {
  return (
    <div className="App">
      <div className="Game">
        <Game />
      </div>
    </div>
  );
}



export default App;
